mapper.select:
	SELECT 
      mtr_tipo_firma_id , 
      codigo_tipo_firma , 
      descripcion , 
      orden 
	FROM
	mtr_tipo_firma
	WHERE 1 = 1

		AND mtr_tipo_firma_id = #{id, jdbcType=INTEGER}


		AND codigo_tipo_firma = #{codigoTipoFirma}


		AND descripcion = #{descripcion}


		AND orden = #{orden, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_tipo_firma( mtr_tipo_firma_id , codigo_tipo_firma , descripcion , orden ) (
SELECT #{element.id} , #{element.codigoTipoFirma} , #{element.descripcion} , #{element.orden} from dual
)

mapper.delete:
    DELETE FROM mtr_tipo_firma
	WHERE 1 = 1

		AND mtr_tipo_firma_id = #{id, jdbcType=INTEGER}


		AND codigo_tipo_firma = #{codigoTipoFirma}


		AND descripcion = #{descripcion}


		AND orden = #{orden, jdbcType=INTEGER}
