mapper.select:
	SELECT 
      mtr_tipo_imputacion_id , 
      codigo_tipo_imputacion , 
      descripcion 
	FROM
	mtr_tipo_imputacion
	WHERE 1 = 1

		AND mtr_tipo_imputacion_id = #{id, jdbcType=INTEGER}


		AND codigo_tipo_imputacion = #{codigoTipoImputacion}


		AND descripcion = #{descripcion}

mapper.insert:
INSERT INTO mtr_tipo_imputacion( mtr_tipo_imputacion_id , codigo_tipo_imputacion , descripcion ) (
SELECT #{element.id} , #{element.codigoTipoImputacion} , #{element.descripcion} from dual
)

mapper.delete:
    DELETE FROM mtr_tipo_imputacion
	WHERE 1 = 1

		AND mtr_tipo_imputacion_id = #{id, jdbcType=INTEGER}


		AND codigo_tipo_imputacion = #{codigoTipoImputacion}


		AND descripcion = #{descripcion}
