mapper.select:
	SELECT 
      mtr_tipo_informacion_noticia_id , 
      carpeta_id , 
      descripcion , 
      icon_text 
	FROM
	mtr_tipo_informacion_noticia
	WHERE 1 = 1

		AND mtr_tipo_informacion_noticia_id = #{id, jdbcType=INTEGER}


		AND carpeta_id = #{carpetaId}


		AND descripcion = #{descripcion}


		AND icon_text = #{iconText}

mapper.insert:
INSERT INTO mtr_tipo_informacion_noticia( mtr_tipo_informacion_noticia_id , carpeta_id , descripcion , icon_text ) (
SELECT #{element.id} , #{element.carpetaId} , #{element.descripcion} , #{element.iconText} from dual
)

mapper.delete:
    DELETE FROM mtr_tipo_informacion_noticia
	WHERE 1 = 1

		AND mtr_tipo_informacion_noticia_id = #{id, jdbcType=INTEGER}


		AND carpeta_id = #{carpetaId}


		AND descripcion = #{descripcion}


		AND icon_text = #{iconText}
