mapper.select:
	SELECT 
      mtr_unidad_medida_id , 
      codigo_sap , 
      descripcion 
	FROM
	mtr_unidad_medida
	WHERE 1 = 1

		AND mtr_unidad_medida_id = #{id, jdbcType=INTEGER}


		AND codigo_sap = #{codigoSap}


		AND descripcion = #{descripcion}

mapper.insert:
INSERT INTO mtr_unidad_medida( mtr_unidad_medida_id , codigo_sap , descripcion ) (
SELECT #{element.id} , #{element.codigoSap} , #{element.descripcion} from dual
)

mapper.delete:
    DELETE FROM mtr_unidad_medida
	WHERE 1 = 1

		AND mtr_unidad_medida_id = #{id, jdbcType=INTEGER}


		AND codigo_sap = #{codigoSap}


		AND descripcion = #{descripcion}
