mapper.select:
	SELECT 
      mtr_usuario_facturacion_id , 
      codigo_usuario_sap , 
      nombres , 
      apellidos , 
      email , 
      codigo_idp , 
      mtr_sector_id , 
      indicador_usuario_cupa , 
      mtr_tipo_gerencia_id , 
      indicador_usuario_aprobador0 , 
      indicador_usuario_firmante 
	FROM
	mtr_usuario_facturacion
	WHERE 1 = 1

		AND mtr_usuario_facturacion_id = #{id, jdbcType=INTEGER}


		AND codigo_usuario_sap = #{codigoUsuarioSap}


		AND nombres = #{nombres}


		AND apellidos = #{apellidos}


		AND email = #{email}


		AND codigo_idp = #{codigoIdp}


		AND mtr_sector_id = #{mtrSector.id, jdbcType=INTEGER}


		AND indicador_usuario_cupa = #{indicadorUsuarioCupa}


		AND mtr_tipo_gerencia_id = #{mtrTipoGerencia.id, jdbcType=INTEGER}


		AND indicador_usuario_aprobador0 = #{indicadorUsuarioAprobador0}


		AND indicador_usuario_firmante = #{indicadorUsuarioFirmante}

mapper.insert:
INSERT INTO mtr_usuario_facturacion( mtr_usuario_facturacion_id , codigo_usuario_sap , nombres , apellidos , email , codigo_idp , mtr_sector_id , indicador_usuario_cupa , mtr_tipo_gerencia_id , indicador_usuario_aprobador0 , indicador_usuario_firmante ) (
SELECT #{element.id} , #{element.codigoUsuarioSap} , #{element.nombres} , #{element.apellidos} , #{element.email} , #{element.codigoIdp} , #{element.mtrSector.id} , #{element.indicadorUsuarioCupa} , #{element.mtrTipoGerencia.id} , #{element.indicadorUsuarioAprobador0} , #{element.indicadorUsuarioFirmante} from dual
)

mapper.delete:
    DELETE FROM mtr_usuario_facturacion
	WHERE 1 = 1

		AND mtr_usuario_facturacion_id = #{id, jdbcType=INTEGER}


		AND codigo_usuario_sap = #{codigoUsuarioSap}


		AND nombres = #{nombres}


		AND apellidos = #{apellidos}


		AND email = #{email}


		AND codigo_idp = #{codigoIdp}


		AND mtr_sector_id = #{mtrSector.id}


		AND indicador_usuario_cupa = #{indicadorUsuarioCupa}


		AND mtr_tipo_gerencia_id = #{mtrTipoGerencia.id}


		AND indicador_usuario_aprobador0 = #{indicadorUsuarioAprobador0}


		AND indicador_usuario_firmante = #{indicadorUsuarioFirmante}
