mapper.select:
	SELECT 
      cer_vendedor_id , 
      mtr_proveedor_id , 
      nro_documento , 
      telefono , 
      nombre , 
      apellido_parterno , 
      apellido_materno , 
      estado 
	FROM
	mtr_vendedor
	WHERE 1 = 1

		AND cer_vendedor_id = #{id, jdbcType=INTEGER}


		AND mtr_proveedor_id = #{mtrProveedor.id, jdbcType=INTEGER}


		AND nro_documento = #{nroDocumento}


		AND telefono = #{telefono}


		AND nombre = #{nombre}


		AND apellido_parterno = #{apellidoParterno}


		AND apellido_materno = #{apellidoMaterno}


		AND estado = #{estado}

mapper.insert:
INSERT INTO mtr_vendedor( cer_vendedor_id , mtr_proveedor_id , nro_documento , telefono , nombre , apellido_parterno , apellido_materno , estado ) (
SELECT #{element.id} , #{element.mtrProveedor.id} , #{element.nroDocumento} , #{element.telefono} , #{element.nombre} , #{element.apellidoParterno} , #{element.apellidoMaterno} , #{element.estado} from dual
)

mapper.delete:
    DELETE FROM mtr_vendedor
	WHERE 1 = 1

		AND cer_vendedor_id = #{id, jdbcType=INTEGER}


		AND mtr_proveedor_id = #{mtrProveedor.id}


		AND nro_documento = #{nroDocumento}


		AND telefono = #{telefono}


		AND nombre = #{nombre}


		AND apellido_parterno = #{apellidoParterno}


		AND apellido_materno = #{apellidoMaterno}


		AND estado = #{estado}
