mapper.select:
	SELECT 
      PARTICIPANTE_ID , 
      NOMBRE , 
      APELLIDO , 
      SEXO , 
      EDAD , 
      PROFESION , 
      ROL 
	FROM
	PARTICIPANTE
	WHERE 1 = 1

		AND PARTICIPANTE_ID = #{id, jdbcType=INTEGER}


		AND NOMBRE = #{nombre}


		AND APELLIDO = #{apellido}


		AND SEXO = #{sexo}


		AND EDAD = #{edad, jdbcType=INTEGER}


		AND PROFESION = #{profesion}


		AND ROL = #{rol}

mapper.insert:
INSERT INTO PARTICIPANTE( PARTICIPANTE_ID , NOMBRE , APELLIDO , SEXO , EDAD , PROFESION , ROL ) (
SELECT #{element.id} , #{element.nombre} , #{element.apellido} , #{element.sexo} , #{element.edad} , #{element.profesion} , #{element.rol} from dual
)

mapper.delete:
    DELETE FROM PARTICIPANTE
	WHERE 1 = 1

		AND PARTICIPANTE_ID = #{id, jdbcType=INTEGER}


		AND NOMBRE = #{nombre}


		AND APELLIDO = #{apellido}


		AND SEXO = #{sexo}


		AND EDAD = #{edad, jdbcType=INTEGER}


		AND PROFESION = #{profesion}


		AND ROL = #{rol}
