mapper.select:
	SELECT 
      id_estado_proveedor , 
      descripcion_estado_proveedor , 
      orden , 
      codigo_estado_proveedor , 
      ind_ver_validacion,
	  ind_ver_licitacion
	FROM
	estado_proveedor
	WHERE 1 = 1

		AND id_estado_proveedor = #{id, jdbcType=INTEGER}


		AND descripcion_estado_proveedor = #{descripcionEstadoProveedor}


		AND orden = #{orden, jdbcType=INTEGER}


		AND codigo_estado_proveedor = #{codigoEstadoProveedor}


		AND ind_ver_validacion = #{indVerValidacion}


		AND ind_ver_licitacion = #{indVerLicitacion}

mapper.insert:
INSERT INTO estado_proveedor( id_estado_proveedor , descripcion_estado_proveedor , orden , codigo_estado_proveedor , ind_ver_validacion , ind_ver_licitacion ) (
SELECT #{element.id} , #{element.descripcionEstadoProveedor} , #{element.orden} , #{element.codigoEstadoProveedor} , #{element.indVerValidacion} , #{element.indVerLicitacion} from dummy
)

mapper.delete:
    DELETE FROM estado_proveedor
	WHERE 1 = 1

		AND id_estado_proveedor = #{id, jdbcType=INTEGER}


		AND descripcion_estado_proveedor = #{descripcionEstadoProveedor}


		AND orden = #{orden, jdbcType=INTEGER}


		AND codigo_estado_proveedor = #{codigoEstadoProveedor}


		AND ind_ver_validacion = #{indVerValidacion}


		and ind_ver_licitacion = #{indVerLicitacion},
