mapper.select:
	SELECT 
      id_peso_linea_comercial , 
      porcentaje_peso_homologacion , 
      porcentaje_peso_condicion_lic , 
      porcentaje_peso_moneda 
	FROM
	peso_linea_comercial
	WHERE 1 = 1

		AND id_peso_linea_comercial = #{id, jdbcType=INTEGER}


		AND porcentaje_peso_homologacion = #{porcentajePesoHomologacion, jdbcType=NUMERIC}


		AND porcentaje_peso_condicion_lic = #{porcentajePesoCondicionLic, jdbcType=NUMERIC}


		AND porcentaje_peso_moneda = #{porcentajePesoMoneda, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO peso_linea_comercial( id_peso_linea_comercial , porcentaje_peso_homologacion , porcentaje_peso_condicion_lic , porcentaje_peso_moneda ) (
SELECT #{element.id} , #{element.porcentajePesoHomologacion} , #{element.porcentajePesoCondicionLic} , #{element.porcentajePesoMoneda} from dummy
)

mapper.delete:
    DELETE FROM peso_linea_comercial
	WHERE 1 = 1

		AND id_peso_linea_comercial = #{id, jdbcType=INTEGER}


		AND porcentaje_peso_homologacion = #{porcentajePesoHomologacion, jdbcType=NUMERIC}


		AND porcentaje_peso_condicion_lic = #{porcentajePesoCondicionLic, jdbcType=NUMERIC}


		AND porcentaje_peso_moneda = #{porcentajePesoMoneda, jdbcType=NUMERIC}
