mapper.select:
	SELECT 
      id_proveedor_adjunto_sunat , 
      archivo_id , 
      archivo_nombre , 
      archivo_tipo , 
      ruta_adjunto 
	FROM
	proveedor_adjunto_sunat
	WHERE 1 = 1

		AND id_proveedor_adjunto_sunat = #{id, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND ruta_adjunto = #{rutaAdjunto}

mapper.insert:
