mapper.select:
	SELECT 
      x.id_proveedor_cliente ,
      x.codigo_tipo_proveedor_cliente ,
      x.razon_social ,
      x.ruc ,
      x.rubro ,
      x.porcentaje_participacion ,
      x.persona_contacto ,
      x.telefono ,
      x.email ,
	  param.valor as codigo_tipo_proveedor_cliente_text
	FROM
	proveedor_cliente x
	left join parametros param on (
		param.modulo = 'PROVEEDOR'
		and param.tipo = 'TIPO_CLIENTE_PROVEEDOR'
		and x.codigo_tipo_proveedor_cliente = param.codigo
	)
	WHERE 1 = 1

		AND x.id_proveedor_cliente = #{id, jdbcType=INTEGER}


		AND x.id_proveedor = #{idBuscarProveedor, jdbcType=INTEGER}


		AND x.codigo_tipo_proveedor_cliente = #{codigoTipoProveedorCliente}


		AND x.razon_social = #{razonSocial}


		AND x.ruc = #{ruc}


		AND x.rubro = #{rubro}


		AND x.porcentaje_participacion = #{porcentajeParticipacion, jdbcType=NUMERIC}


		AND x.persona_contacto = #{personaContacto}


		AND x.telefono = #{telefono}


		AND x.email = #{email}

mapper.insert:
INSERT INTO proveedor_cliente( id_proveedor_cliente , codigo_tipo_proveedor_cliente , razon_social , ruc , rubro , porcentaje_participacion , persona_contacto , telefono , email ) (
SELECT #{element.id} , #{element.codigoTipoProveedorCliente} , #{element.razonSocial} , #{element.ruc} , #{element.rubro} , #{element.porcentajeParticipacion} , #{element.personaContacto} , #{element.telefono} , #{element.email} from dummy
)

mapper.delete:
    DELETE FROM proveedor_cliente
	WHERE 1 = 1

		AND id_proveedor_cliente = #{id, jdbcType=INTEGER}


		AND codigo_tipo_proveedor_cliente = #{codigoTipoProveedorCliente}


		AND razon_social = #{razonSocial}


		AND ruc = #{ruc}


		AND rubro = #{rubro}


		AND porcentaje_participacion = #{porcentajeParticipacion, jdbcType=NUMERIC}


		AND persona_contacto = #{personaContacto}


		AND telefono = #{telefono}


		AND email = #{email}
