mapper.select:
	SELECT 
      id_proveedor_cuenta_bancaria_extranj , 
      nombre_benef , 
      codigo_tipo_cuenta_benef , 
      cuenta_benef , 
      direccion_benef , 
      region_pais_benef , 
      ciudad_pais_benef , 
      referencia_benef , 
      nombre_banco_pagador , 
      region_pais_banco_pagador , 
      ciudad_pais_banco_pagador , 
      direccion_banco_pagador , 
      tipo_codigo_banco_pagador , 
      codigo_banco_pagador , 
      nombre_banco_interme , 
      region_pais_banco_interme , 
      ciudad_pais_banco_interme , 
      tipo_codigo_banco_interme , 
      codigo_banco_interme , 
      codigo_banco_pagador_interme 
	FROM
	proveedor_cuenta_bancaria_extranj
	WHERE 1 = 1

		AND id_proveedor_cuenta_bancaria_extranj = #{id, jdbcType=INTEGER}


		AND nombre_benef = #{nombreBenef}


		AND codigo_tipo_cuenta_benef = #{codigoTipoCuentaBenef}


		AND cuenta_benef = #{cuentaBenef}


		AND direccion_benef = #{direccionBenef}


		AND region_pais_benef = #{regionPaisBenef}


		AND ciudad_pais_benef = #{ciudadPaisBenef}


		AND referencia_benef = #{referenciaBenef}


		AND nombre_banco_pagador = #{nombreBancoPagador}


		AND region_pais_banco_pagador = #{regionPaisBancoPagador}


		AND ciudad_pais_banco_pagador = #{ciudadPaisBancoPagador}


		AND direccion_banco_pagador = #{direccionBancoPagador}


		AND tipo_codigo_banco_pagador = #{tipoCodigoBancoPagador}


		AND codigo_banco_pagador = #{codigoBancoPagador}


		AND nombre_banco_interme = #{nombreBancoInterme}


		AND region_pais_banco_interme = #{regionPaisBancoInterme}


		AND ciudad_pais_banco_interme = #{ciudadPaisBancoInterme}


		AND tipo_codigo_banco_interme = #{tipoCodigoBancoInterme}


		AND codigo_banco_interme = #{codigoBancoInterme}


		AND codigo_banco_pagador_interme = #{codigoBancoPagadorInterme}

