mapper.select:
	SELECT 
      id_proveedor_funcionario , 
      apellidos_nombres , 
      cargo , 
      ruc 
	FROM
	proveedor_funcionario
	WHERE 1 = 1

		AND id_proveedor_funcionario = #{id, jdbcType=INTEGER}


		AND id_proveedor = #{idBuscarProveedor, jdbcType=INTEGER}


		AND apellidos_nombres = #{apellidosNombres}


		AND cargo = #{cargo}


		AND ruc = #{ruc}

mapper.insert:
INSERT INTO proveedor_funcionario( id_proveedor_funcionario , apellidos_nombres , cargo , ruc ) (
SELECT #{element.id} , #{element.apellidosNombres} , #{element.cargo} , #{element.ruc} from dummy
)

mapper.delete:
    DELETE FROM proveedor_funcionario
	WHERE 1 = 1

		AND id_proveedor_funcionario = #{id, jdbcType=INTEGER}


		AND apellidos_nombres = #{apellidosNombres}


		AND cargo = #{cargo}


		AND ruc = #{ruc}
