mapper.select:
	SELECT 
      x.id_proveedor_instalacion ,
      x.codigo_tipo_instalacion ,
      x.direccion ,
      x.telefono,
	  param.valor as codigo_tipo_instalacion_text
	FROM
	proveedor_instalacion x
	left join parametros param on (
		param.modulo = 'PROVEEDOR'
		and param.tipo = 'TIPO_INSTALACION'
		and x.codigo_tipo_instalacion = param.codigo
	)

	WHERE 1 = 1

		AND x.id_proveedor_instalacion = #{id, jdbcType=INTEGER}


		AND x.id_proveedor = #{idBuscarProveedor, jdbcType=INTEGER}


		AND x.codigo_tipo_instalacion = #{codigoTipoInstalacion}


		AND x.direccion = #{direccion}


		AND x.telefono = #{telefono}

mapper.insert:
INSERT INTO proveedor_instalacion( id_proveedor_instalacion , codigo_tipo_instalacion , direccion , telefono ) (
SELECT #{element.id} , #{element.codigoTipoInstalacion} , #{element.direccion} , #{element.telefono} from dummy
)

mapper.delete:
    DELETE FROM proveedor_instalacion
	WHERE 1 = 1

		AND id_proveedor_instalacion = #{id, jdbcType=INTEGER}


		AND codigo_tipo_instalacion = #{codigoTipoInstalacion}


		AND direccion = #{direccion}


		AND telefono = #{telefono}
