			#{item}

		)


		AND EXISTS (SELECT 1 FROM PROVEEDOR_LINEA_COMERCIAL lc WHERE lc.ID_PROVEEDOR = p.ID_PROVEEDOR AND lc.ID_SUBFAMILIA IN

