#{item}
)
AND tp.ID_TIPO_PROVEEDOR = p.ID_TIPO_PROVEEDOR AND pr.MODULO = 'PROVEEDOR' AND pr.TIPO = 'TIPO_PERSONA' AND pr.CODIGO = p.TIPO_PERSONA AND p.ID_ESTADO_PROVEEDOR = es.ID_ESTADO_PROVEEDOR AND (es.IND_VER_LICITACION = '1' or p.IND_MIGRADO_SAP = '1')
ORDER BY p.razon_social, p.evaluacion_homologacion desc

