mapper.update:
UPDATE proveedor
set id_estado_proveedor = #{idEstadoProveedor}
where id_proveedor = #{idProveedor}

UPDATE proveedor
set id_estado_proveedor = #{ idEstadoProveedor } ,
	en_flujo_aprobacion = #{ enFlujoAprobacion }
where id_proveedor = #{idProveedor}
