mapper.select:
	SELECT 
      x.id_proveedor_permiso ,
      x.codigo_tipo_permiso ,
      x.entidad_emisora ,
      x.vigencia,
	  param.valor as vigencia_text
	FROM
	proveedor_permiso x
	left join parametros param on (
		param.modulo = 'PROVEEDOR'
		and param.tipo = 'VIGENCIA'
		and x.vigencia = param.codigo
	)

	WHERE 1 = 1

		AND x.id_proveedor_permiso = #{id, jdbcType=INTEGER}


		AND x.id_proveedor = #{idBuscarProveedor, jdbcType=INTEGER}


		AND x.codigo_tipo_permiso = #{codigoTipoPermiso}


		AND x.entidad_emisora = #{entidadEmisora}


		AND x.vigencia = #{vigencia}

mapper.insert:
INSERT INTO proveedor_permiso( id_proveedor_permiso , codigo_tipo_permiso , entidad_emisora , vigencia ) (
SELECT #{element.id} , #{element.codigoTipoPermiso} , #{element.entidadEmisora} , #{element.vigencia} from dummy
)

mapper.delete:
    DELETE FROM proveedor_permiso
	WHERE 1 = 1

		AND id_proveedor_permiso = #{id, jdbcType=INTEGER}


		AND codigo_tipo_permiso = #{codigoTipoPermiso}


		AND entidad_emisora = #{entidadEmisora}


		AND vigencia = #{vigencia}
