mapper.select:
	SELECT 
      id_proveedor_por_validar_info , 
      nombre_campo_bd , 
      ind_es_lista , 
      valor_actual , 
      valor_nuevo , 
      valor_lista_actual , 
      valor_lista_nuevo 
	FROM
	proveedor_por_validar_info
	WHERE 1 = 1

		AND id_proveedor_por_validar_info = #{id, jdbcType=INTEGER}


		AND nombre_campo_bd = #{nombreCampoBd}


		AND ind_es_lista = #{indEsLista}


		AND valor_actual = #{valorActual}


		AND valor_nuevo = #{valorNuevo}


		AND valor_lista_actual = #{valorListaActual}


		AND valor_lista_nuevo = #{valorListaNuevo}

