mapper.select:
	SELECT 
      x.id_proveedor_pregunta_informacion ,
      x.respuesta
	FROM
	  proveedor_pregunta_informacion x
	WHERE 1 = 1

		AND x.id_proveedor_pregunta_informacion = #{id, jdbcType=INTEGER}


		AND x.id_proveedor = #{idBuscarProveedor, jdbcType=INTEGER}


		AND x.id_pregunta_informacion = #{idBuscarPreguntaInformacion, jdbcType=INTEGER}


		AND x.respuesta = #{respuesta}

mapper.insert:
INSERT INTO  proveedor_pregunta_informacion(
id_proveedor_pregunta_informacion , 
respuesta 
)
VALUES
(
#{element.id} , 
#{element.respuesta} 
)

INSERT INTO  proveedor_pregunta_informacion(
id_proveedor_pregunta_informacion , 
respuesta 
)
(

SELECT
#{element.id} , 
#{element.respuesta} 
from dummy


)

mapper.update:
UPDATE proveedor_pregunta_informacion
respuesta = #{respuesta},
WHERE id_proveedor_pregunta_informacion = #{id, jdbcType=INTEGER}

mapper.delete:
    DELETE FROM proveedor_pregunta_informacion
	WHERE 1 = 1

		AND id_proveedor_pregunta_informacion = #{id, jdbcType=INTEGER}


		AND respuesta = #{respuesta}
