mapper.select:
	SELECT 
      id_proveedor_validacion_info , 
      fecha_aprobacion_rechazo , 
      usuario_aprobacion_rechazo , 
      ind_aprobacion_rechazo , 
      motivo_rechazo 
	FROM
	proveedor_validacion_info
	WHERE 1 = 1

		AND id_proveedor_validacion_info = #{id, jdbcType=INTEGER}


		AND fecha_aprobacion_rechazo = #{fechaAprobacionRechazo, jdbcType=TIMESTAMP}


		AND usuario_aprobacion_rechazo = #{usuarioAprobacionRechazo}


		AND ind_aprobacion_rechazo = #{indAprobacionRechazo}


		AND motivo_rechazo = #{motivoRechazo}

mapper.insert:
INSERT INTO proveedor_validacion_info( id_proveedor_validacion_info , fecha_aprobacion_rechazo , usuario_aprobacion_rechazo , ind_aprobacion_rechazo , motivo_rechazo ) (
SELECT #{element.id} , #{element.fechaAprobacionRechazo} , #{element.usuarioAprobacionRechazo} , #{element.indAprobacionRechazo} , #{element.motivoRechazo} from dummy
)

mapper.delete:
    DELETE FROM proveedor_validacion_info
	WHERE 1 = 1

		AND id_proveedor_validacion_info = #{id, jdbcType=INTEGER}


		AND fecha_aprobacion_rechazo = #{fechaAprobacionRechazo, jdbcType=TIMESTAMP}


		AND usuario_aprobacion_rechazo = #{usuarioAprobacionRechazo}


		AND ind_aprobacion_rechazo = #{indAprobacionRechazo}


		AND motivo_rechazo = #{motivoRechazo}
