    @RequestMapping(value = "/certificado/numFirmaA",
            method = RequestMethod.GET, produces = {
            MediaType.APPLICATION_JSON_VALUE})
    public ResponseEntity<?> numeroResultadosA(HttpServletRequest request) {

        UserSession userSession = this.systemLoggedUser.getUserSession();
        log.error("numFirmaA  userSession " + userSession);
        MtrEstado estado = this.mtrEstadoDeltaRepository.getByCodigoAgrupadoAndCodigoEstado("CE", "CEFA");
        Integer numReg = 0;
        List<CerCertificado> lista = this.cerCertificadoDeltaRepository.findByByStatus(estado.getId());
        List<CerFirma> listaConfig = this.cerFirmaDeltaRepository.findByTipoYResponsable("FA", userSession.getRuc());

        if(lista != null && lista.size() > 0) {

            for(CerCertificado ele: lista) {
                Optional<CerFirma> optCerFirma = listaConfig.stream()
                        .filter(x -> x.getCerNotaPedido().getId() == ele.getCerNotaPedido().getId())
                        .findFirst();
                if(optCerFirma.isPresent())
                    numReg++;
            }

        }
        //=============================================
        Map<String, TileDynamic> map = new HashMap<>();

        //Inicio
        TileDynamic tile = new TileDynamic();
        Integer contador = numReg;

        tile.setIcon("sap-icon://activities");
        if (contador.intValue() <= 0) {
            tile.setInfo("Num. Pendiente");
        }
        else {
            if (contador.intValue() == 1) {
                tile.setInfo("Num. Pendiente");
            }
            else {
                tile.setInfo("Num. Pendiente");
            }
        }
        tile.setInfoState("Neutral");
        tile.setNumber(new BigDecimal(contador));
        tile.setNumberDigits(1);
        tile.setNumberFactor("");
        tile.setNumberState("Neutral");
        tile.setNumberUnit("");
        //tile.setStateArrow("UP");
        tile.setSubtitle("");
        tile.setTitle("Aprobación Firma A");

       // return tile;
        //Fin


        map.put("d", tile);
        return new ResponseEntity<>(map, HttpStatus.OK);
    }

